import React from "react";
import CourseRowComponents from "./CourseRowComponents";
import {findAllCourses, updateCourse, deleteCourse, createCourse} from "../Services/CourseServices";


class CourseListComponents extends React.Component{

    state = {
        courses: [
            {
                _id: "123",
                title: "CS5610",
                owner:"me",
                modified:"yesterday"
            },
            {
                _id: "234",
                title: "CS4500",
                owner:"me",
                modified:"yesterday"
            },
            {
                _id: "456",
                title: "CS4800",
                owner:"me",
                modified:"yesterday"
            },
            {
                _id: "345",
                title: "CS3200",
                owner:"me",
                modified:"yesterday"
            }
        ]
    }

    componentDidMount() {
    }

    deleteCourse = (course) => {
        this.setState(prevState => ({
            courses:prevState.courses.filter(c => c._id !== course._id)})
        )
    }

    addCourse = () => {
        const newCourse = {
            _id: (new Date()).getMilliseconds()+"",
            title:"new Course",
            owner:"me",
            modified:(new Date()).toDateString()
        }
        this.setState(prevState => ({
            courses: [
                ...prevState.courses, newCourse
                ]
        }))
    }


    render(){
        return(
            <div>
                <h1>Course List(for {this.props.instructor}) {this.props.term}</h1>
                <table className="table">
                    {
                        this.state.courses.map(course =>
                            <courseRowComponent
                                deleteCourse={this.deleteCourse}
                                course={course}/>
                        )
                    }
                </table>
                <button
                    onClick={this.addCourse}
                    className="btn btn-success">
                    Add Course
                </button>
            </div>
        );
    }
}

export default CourseListComponents;
