import React from "react";
import {connect} from "react-redux";
import {
    createWidget,
    deleteWidget,
    updateWidget,
    editWidget,
    okWidget} from "../actions/widgetActions";

const WidgetList = ({
                        widgets=[],
                        deleteWidget,
                        createWidget,
                        updateWidget,
                        editWidget,
                        okWidget}) =>
    <div>
        <h1>Widgets</h1>
        <ul>
            {
                widgets.map(widget =>
                        <li>
                            <button
                                onClick={() => deleteWidget(widget)}>
                                Delete
                            </button>
                            {
                                widget.editing &&
                                <span><input
                                    onChange={(event) => updateWidget({
                                        ...widget,
                                        name: event.target.value
                                    })}
                                    value={widget.name}/>
                <button onClick={() => okWidget(widget)}>
                  Ok
                </button>
                </span>
                            }
                            {
                                !widget.editing &&
                                <span>
                  {widget.name}
                                    <button onClick={() => editWidget(widget)}>
                    Edit
                  </button>
                </span>
                            }
                        </li>
                )
            }
        </ul>
        <button onClick={createWidget}>Create</button>
    </div>

// export default WidgetList

const stateToPropertyMapper = (state) => ({
    widgets: state.widgetsReducer.widgets
})

const propertyToDispatchMapper = (dispatch) => ({
    deleteWidget: (widget) => deleteWidget(dispatch, widget),
    createWidget: () => createWidget(dispatch),
    updateWidget: (widget) => updateWidget(dispatch, widget),
    editWidget: (widget) => editWidget(dispatch, widget),
    okWidget: (widget) => okWidget(dispatch, widget)
})

export default connect
( stateToPropertyMapper,
    propertyToDispatchMapper)
(WidgetList)
